import { useState, useEffect } from 'react';
import Blogs from './components/Blogs';
import BlogForm from './components/BlogForm';
import blogServices from './services/blogServices';
import blogi from './teams.json';

const App = () => {

  let [blogs, setBlogs] = useState([])
  const [newTitle, setNewTitle] = useState('')
  const [newAuthor, setNewAuthor] = useState('')
  const [newUrl, setNewUrl] = useState('')
  const [newLikes, setNewLikes] = useState(0)

  useEffect(() => {
    blogServices.getAll().then(response => {
      setBlogs(response.data)
    })
  }, [])

  useEffect(() => {
    setBlogs(blogi)
  }, [])

  /*const incrementVoteCount = (blogId, id) => {
    blogs = blogs.map((blog) => {
      if (blog._id === blogId) {
        blog.likes = blog.likes + 1;
      }
      return blog;
    });
    setBlogs(blogs);
  }*/

  const incrementVoteCount = (id) => {
    const blog = blogs.find(b => b.id === id)
    const changedBlog = {...blog, likes: + 1}
    console.log(changedBlog)
    blogServices.update(id, changedBlog).then(setBlogs(changedBlog))
  }

  return (
    <div className="App">
      <h1>Add blog</h1>
      <BlogForm blogs={blogs} setBlogs={setBlogs} newTitle={newTitle} newAuthor={newAuthor} newUrl={newUrl} newLikes={newLikes} setNewTitle={setNewTitle} setNewAuthor={setNewAuthor} setNewUrl={setNewUrl} setNewLikes={setNewLikes} />
      <h1>Blogs</h1>
      <Blogs blogs={blogs} newLikes={newLikes} setBlogs={setBlogs} setNewLikes={setNewLikes} incrementVoteCount={incrementVoteCount} />
    </div>
  );
}

export default App
