import { useState } from "react"

const Blogs = (props) => {

    const Content = (props) => {

        const [like, setLike] = useState(0)

        const incrementLike = (id) => {
            props.blogs = props.blogs.map(blog => {
                if (blog._id === id) {
                    props.blogsLikes = props.blogsLikes + 1
                }
                return blog
            })
            props.setNewLikes(props.blogs)
        }

        return (
            <div>
                <h3>{props.blogsTitle}</h3>
                <h4>{props.blogsAuthor}</h4>
                <p>{props.blogsUrl}</p>
                <p>{props.blogsLikes}</p>
                <button onClick={() => incrementLike(props.id)}>Vote</button>
            </div>
        )
    }

    return (
        <div>
            {props.blogs.map(blog => <Content key={blog._id} id={blog._id} blogsTitle={blog.title} blogsAuthor={blog.author} blogsUrl={blog.url} blogsLikes={blog.likes} newLikes={props.newLikes} setNewLikes={props.setNewLikes} />)}
        </div>
    )
}

export default Blogs
