      url: "test",
      likes: "1"
    },
    {
      title: "test",
      author: "test",
      url: "test",
      likes: "2"
    },
    {
      title: "test",
      author: "test",
      url: "test",
      likes: "3"
    }
  ]*/

  return (
    <div className="App">
      <h1>Add blog</h1>
      <BlogForm blogs={blogs} setBlogs={setBlogs} newTitle={newTitle} newAuthor={newAuthor} newUrl={newUrl} setNewTitle={setNewTitle} setNewAuthor={setNewAuthor} setNewUrl={setNewUrl} />
      <h1>Blogs</h1>
      <Blogs blogs={blogs} />
    </div>
  );
}

export default App
