const Blogs = (props) => {

    const Content = (props) => {

        const incrementVote = (id) => {
            console.log(props.blogs.likes)
            props.blogs = props.blogs.map(blog => {
                if (blog._id === id) {
                    blog.likes = blog.likes +1
                }

                return blog
            })
            props.setBlogs(props.blogs)
        }

        return (
            <div>
                <h3>{props.blogsTitle}</h3>
                <h4>{props.blogsAuthor}</h4>
                <p>{props.blogsUrl}</p>
                <p>{props.blogsLikes}</p>
                <button onClick={incrementVote()}>Vote</button>
            </div>
        )
    }

    return (
        <div>
            {props.blogs.map(blog => <Content blogsTitle={blog.title} blogsAuthor={blog.author} blogsUrl={blog.url} blogsLikes={blog.likes} />)}
        </div>
    )
}

export default Blogs
